parameters.jsCode:
const body = $json.body || $json;
const caseId = body.case_id || body.caseId;
const sessionId = body.session_id || body.sessionId;

if (!caseId && !sessionId) {
  throw new Error('Either case_id or session_id is required');
}

return {
  json: {
    caseId: caseId,
    sessionId: sessionId,
    lookupBy: caseId ? 'case_id' : 'session_id'
  }
};

const d = $json;

function arr(a) { return Array.isArray(a) && a.length ? a.join(', ') : '-'; }
function refList(a) {
  if (!Array.isArray(a) || !a.length) return '-';
  return a.map(x => {
    if (typeof x === 'string') return `- ${x}`;
    return `- ${x.artist || ''} ‚Äî ${x.title || ''} ${x.notes ? '(' + x.notes + ')' : ''}`;
  }).join('\n');
}
function missList(a) {
  if (!Array.isArray(a) || !a.length) return '- None';
  return a.map(x => `- ${x}`).join('\n');
}

// Format budget
let budgetStr = '-';
if (d.budget_min && d.budget_max && d.budget_min !== d.budget_max) {
  budgetStr = `${d.budget_min} - ${d.budget_max}`;
} else if (d.budget_min) {
  budgetStr = String(d.budget_min);
} else if (d.budget_raw) {
  budgetStr = d.budget_raw;
}

// Format dates
function fmtDate(v) { if (!v) return '-'; try { return new Date(v).toISOString().slice(0, 10); } catch(e) { return String(v); } }

const md = `# ${d.catchy_case_id || ''} ‚Äî ${d.case_title || d.project_title || 'Untitled'}

**Case Number:** ${d.case_number || '-'}  
**Version:** ${d.version || 1}  
**Last Updated:** ${new Date().toISOString().slice(0, 10)}  
**Quality:** ${d.brief_quality || 'unknown'} (${Math.round((d.completion_rate || 0))}% complete)  
**Extraction Status:** ${d.extraction_status || 'unknown'}  

---

## 1) Business Brief
- **Client:** ${d.client || '-'}
- **Agency:** ${d.agency || '-'}
- **Brand:** ${d.brand || '-'}
- **Brief Sender:** ${d.brief_sender_name || '-'} ${d.brief_sender_email ? '(' + d.brief_sender_email + ')' : ''} ${d.brief_sender_role ? '‚Äî ' + d.brief_sender_role : ''}
- **Media:** ${arr(d.media)}
- **Term:** ${d.term || '-'}
- **Territory:** ${arr(d.territory)}
- **Budget:** ${budgetStr}
- **Exclusivity:** ${d.exclusivity ? 'Yes' : 'No'} ${d.exclusivity_details ? '‚Äî ' + d.exclusivity_details : ''}
- **Lengths:** ${arr(d.lengths)}
- **Cutdowns:** ${arr(d.cutdowns)}
- **Language Adaptations:** ${arr(d.language_adaptations)}

## 2) Creative Brief
- **Creative Direction:** ${d.creative_direction || '-'}
- **Mood:** ${d.mood || '-'}
- **Keywords:** ${arr(d.keywords)}
- **Genres:** ${arr(d.genres)}
- **Mood Descriptors:** ${arr(d.mood_descriptors)}
- **Instruments:** ${arr(d.instruments)}
- **Vocals:** ${d.vocals_preference || '-'}
- **Must Avoid:** ${d.must_avoid || '-'}

**Reference Tracks:**
${refList(d.reference_tracks)}

**Lyrics Requirements:**  
${d.lyrics_requirements || '-'}

## 3) Context & Audience
- **Target Audience:** ${d.target_audience || '-'}
- **Brand Values:** ${arr(d.brand_values)}

**Campaign Context:**  
${d.campaign_context || '-'}

## 4) Technical Brief
- **Source Type:** ${d.source_type || '-'}
- **Stems Required:** ${d.stems_required ? 'Yes' : 'No'}
- **Sync Points:** ${d.sync_points || '-'}
- **Deliverable Formats:** ${arr(d.deliverable_formats)}
- **File Formats:** ${arr(d.file_formats)}

## 5) Timeline & Deadlines
- **Kick-off:** ${fmtDate(d.kickoff_date)}
- **First Presentation:** ${fmtDate(d.first_presentation_date)}
- **Client Presentation:** ${fmtDate(d.client_presentation_date)}
- **Submission Deadline:** ${fmtDate(d.submission_deadline)}
- **PPM:** ${fmtDate(d.ppm_date)}
- **Shoot:** ${fmtDate(d.shoot_date)}
- **Offline:** ${fmtDate(d.offline_date)}
- **Online:** ${fmtDate(d.online_date)}
- **Air Date:** ${fmtDate(d.air_date)}
- **Urgency:** ${d.deadline_urgency || 'standard'}

---

## Missing Information (${(d.missing_information || []).length})
${missList(d.missing_information)}

## Extraction Notes
${d.extraction_notes || '-'}
`;

return { 
  json: { 
    ...d,
    brief_md: md
  } 
};

const caseData = $('Render Brief Markdown').item.json;
const currentFileUrl = $input.all()[0].json.url;
const versionedFileUrl = $input.all()[1].json.url;

return {
  json: {
    channel: caseData.slack_channel_id,
    text: `‚úÖ Brief synced to Nextcloud (v${caseData.version})\n\nüìÑ Current: ${currentFileUrl}\nüìã Version ${caseData.version}: ${versionedFileUrl}`,
    caseId: caseData.case_id,
    version: caseData.version,
    success: true
  }
};

parameters.query:
INSERT INTO tf_case_activity (case_id, activity_type, activity_description, source, changes)
VALUES ($1::uuid, 'brief_synced', $2, 'nextcloud_sync', $3::jsonb)
RETURNING id;

options.queryReplacement:
={{ [
  $('Build Success Notification').item.json.caseId,
  'Brief synced to Nextcloud v' + $('Build Success Notification').item.json.version,
  JSON.stringify({
    version: $('Build Success Notification').item.json.version,
    sync_type: 'update',
    files_updated: ['current', 'versioned']
  })
] }}

parameters.jsCode:
const caseData = $('Render Brief Markdown').item.json;
const error = $json.error || {};
const errorMessage = error.message || JSON.stringify(error);

return {
  json: {
    channel: caseData.slack_channel_id,
    text: `‚ö†Ô∏è Brief was updated in database but failed to sync to Nextcloud.\n\nError: ${errorMessage}\n\nPlease contact support if this issue persists.`,
    caseId: caseData.case_id,
    error: errorMessage,
    success: false
  }
};

